vec4 CCFragOutput (vec4 color) {
  return color;
}
layout(set = 2, binding = 12) uniform sampler2D cc_spriteTexture;
float randomNum(vec2 p)
{
  return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}
float randomNum(float x){
  return fract(sin(x)* 43758.5453);
}
float valueNoise(vec2 p) {
  vec2 w = floor(p);
  vec2 k = fract(p);
  k = k*k*(3.-2.*k);
  float n = w.x*10. + w.y*48.;
  float a = randomNum(n);
  float b = randomNum(n+10.);
  float c = randomNum(n+48.);
  float d = randomNum(n+58.);
  return mix(
    mix(a, b, k.x),
    mix(c, d, k.x),
    k.y);
}
float fbm6( vec2 p ) {
  const mat2 mtx = mat2( 0.80,  0.60, -0.60,  0.80 );
  float f = 0.0;
  f += 0.500000*valueNoise( p ); p = mtx*p*2.02;
  f += 0.250000*valueNoise( p ); p = mtx*p*2.03;
  f += 0.125000*valueNoise( p ); p = mtx*p*2.01;
  f += 0.062500*valueNoise( p ); p = mtx*p*2.04;
  f += 0.031250*valueNoise( p ); p = mtx*p*2.01;
  f += 0.015625*valueNoise( p );
  return f/0.96875;
}
layout(location = 1) in vec2 uv0;
vec4 getFogCol();
layout(set = 1, binding = 0) uniform Constant {
  vec4 mainColor;
};
vec4 frag () {
  vec4 col = mainColor * texture(cc_spriteTexture, uv0);
  col = getFogCol();
  return CCFragOutput(col);
}
vec4 getFogCol(){
  float t = cc_time.x;
  vec2 uv1 = vec2(uv0)*10.;
  vec2 c1 = vec2(fbm6(uv1+0.55*t));
  float f = fbm6(uv1+c1);
  vec3 rgb = vec3(f);
  rgb = mix(mainColor.rgb,rgb,0.6);
  vec4 col = vec4(rgb,.8);
  return col;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

